<style>
   .converter {
      width: 100vw;
      height: 100vh;
      display: grid;
      justify-content: center;
      padding-top: 85px;
      position: relative;
   }

   .converter__input {
      width: 272px;
      height: 68px;
      background: var(--main-color);
      border: none;
      font-family: monospace;
      font-size: var(--font-size-medium);
      text-align: right;
      padding-right: 10px;
   }

   .converter__equal {
      font-size: var(--font-size-large);
      text-align: center;
      border: solid;
      height: fit-content;
   }

   .converter__output {
      width: 272px;
      height: 68px;
      background: var(--main-color);
      color: var(--faded-text-color);
      border: none;
      font-family: monospace;
      font-size: var(--font-size-medium);
      text-align: right;
      padding-right: 10px;
   }

   .converter__buttons {
      display: flex;
      justify-content: space-between;
      height: fit-content;
      position: relative;
   }

   .converter__buttons button {
      background: none;
      border: none;
   }

   .converter__hamburger {
      z-index: 1;
   }

   .converter__navigation {
      display: none;
      position: absolute;
      overflow: scroll;
      width: 100vw;
      height: 77vh;
      padding: 20px;
      background: var(--main-color);
      top: 210px;
   }

   .navigation__button {
      font-family: monospace;
      font-size: var(--font-size-small);
      margin-bottom: 10px;
   }
</style>

<script>
	export default {
		data() {
			return {
           inputValue: '1',
           outputValue: '',
           trueOrFalse: '',
           multipleCurrency: ''
           
           
         }
      },

	async created() {
		this.fetchNew();
	},

   methods: {
		async fetchNew() {
			
			const url = 'https://cdn.jsdelivr.net/gh/fawazahmed0/currency-api@1/latest/currencies/eur.json';
			const res = await fetch(url);
			const { eur } = await res.json();

			this.outputValue = (this.inputValue * eur.nok).toFixed(2);
         let parsed = JSON.stringify(eur)
         this.multipleCurrency = parsed;
      },

      toggle() {
         this.trueOrFalse = false;
         if (this.trueOrFalse = true) {
            this.trueOrFalse = false;  
         }
         if (this.trueOrFalse = false) {
            this.trueOrFalse = true;
         }
         console.log(this.trueOrFalse)

      }

   }

   

}

</script>
